let matches = new RegExp('(?:https://)*(?:www|m|t)\.wattpad\.com/((?:text-)([0-9]*)-([0-9]*)-([a-z0-9]*)|(?:story/)([0-9]*)|([0-9]*)).*').exec(window.location.href);
if (matches[0].includes('/story/')) {
  return;
}

let title = '';
let content = '';
let nextURL = '';

if (!matches[0].includes('/text')) {
  let prefetched = window.prefetched['part.' + matches[1] + '.metadata'];
  nextURL = prefetched.data.text_url.text;
  return {
    retry: 1,
    nextURL: nextURL,
    extras: prefetched.data.group.parts.map(function(x) { return { id: x.id, title: x.title }; })
  };
} else if (matches[0].includes('/text')) {
  injectJquery();
  if (!extras || extras.length <= 0) {
    return;
  }
  let storyId = matches[2];
  let chapterId = matches[3];
  let uid = matches[4];
  let currentChapter = extras.find(i => i.id == chapterId);
  title = currentChapter.title;
  $('body').html($('body pre')[0].innerText);
  content = '<p>' + $('body')[0].innerText.replace(/\s*\n+\s*/g, '<br>').split('<br>').join('</p><p>') + '</p>';
  let currentChapterIndex = extras.indexOf(currentChapter);
  if (currentChapterIndex + 1 < extras.length) {
    let nextChapterId = extras[currentChapterIndex + 1].id;
    let refresh_token = 'https://api.wattpad.com/v4/parts/' + nextChapterId + '/token';
    nextURL = 'https://t.wattpad.com/text-' + storyId + '-' + nextChapterId + '-' + uid + '?' + JSON.parse(await fetch(refresh_token).then(e => e.text())).token;
  }
}

let retMe = [
  {
    "title": title,
    "content": content,
    "nextURL": nextURL
  },
];

return retMe;

